'use strict';

var Index = function () {
    'use strict';

    var config = function config() {
        configSlider();
        configAutoSlider();
    };

    var configSlider = function configSlider() {
        var spans = Array.from(document.querySelectorAll('.header .slider-circle'));
        spans.forEach(function (element) {
            element.onclick = function (event) {
                return nextImage(event, spans);
            };
        });
    };

    var nextImage = function nextImage(event, spans) {
        spans.forEach(function (s) {
            return s.classList.remove('active');
        });
        var target = event.target || event.srcElement;
        target.classList.add('active');

        document.querySelector('.header .header-slider:not(.hidden)').classList.add('hidden');
        Array.from(document.querySelectorAll('.header .header-slider'))[spans.indexOf(target)].classList.remove('hidden');
    };

    var configAutoSlider = function configAutoSlider() {
        setInterval(function () {
            var spans = Array.from(document.querySelectorAll('.header .slider-circle'));
            var span = spans.filter(function (e) {
                return e.classList.contains('active');
            })[0];
            var index = spans.indexOf(span);

            index++;
            if (index === spans.length) index = 0;

            spans[index].click();
        }, 10000);
    };

    return {
        config: config
    };
}();